import React, { useState, useEffect, useRef} from 'react'


export default function Flashcard({ flashcard }) {
    const [flip, setFlip] = useState(false)
    const [height, setHeight] = useState('initial')
    const frontEl = useRef() 
    const backEl = useRef()


    function setMaxHeight(){
        const frontHeight = frontEl.current.getBoundingClientRect().frontHeight
        const backHeight = backEl.current.getBoundingClientRect().frontHeight

        Math.max(frontHeight, backHeight, 100)
    }

    useEffect(setMaxHeight, [flashcard.question, flashcard.answer])

  return (
    <div 
    className={`card ${flip ? 'flip': ''}`}
    style = {{height: height}}
     onClick={() => setFlip(!flip)}>
        <div className='front' ref={frontEl}>
            “{flashcard.question}”
        </div>
        <div className='back' ref={backEl}>{flashcard.answer}</div>
        </div>
  )
}
